package main

import (
	"fmt"
)

type ringBuffer struct {
	n          int
	buffer     []rune
	next       int
	occurances [26]int
}

func newRingBuffer(n int) *ringBuffer {
	return &ringBuffer{
		n:      n,
		buffer: make([]rune, n),
	}
}

func (rb *ringBuffer) consume(ch rune) {
	prev := rb.buffer[rb.next]
	if prev >= 'a' && prev <= 'z' {
		rb.occurances[prev-'a']--
	}
	rb.buffer[rb.next] = ch
	rb.occurances[ch-'a']++
	rb.next++
	if rb.next >= rb.n {
		rb.next -= rb.n
	}
}

func (rb ringBuffer) isMarker() bool {
	var n int
	for i := 0; i < 26; i++ {
		if rb.occurances[i] > 1 {
			return false
		}
		n += rb.occurances[i]
	}
	return n == rb.n
}

func part1(s string) {
	rb := newRingBuffer(4)
	for i, ch := range s {
		rb.consume(ch)
		if rb.isMarker() {
			fmt.Println(i + 1)
			return
		}
	}
}

func part2(s string) {
	rb := newRingBuffer(14)
	for i, ch := range s {
		rb.consume(ch)
		if rb.isMarker() {
			fmt.Println(i + 1)
			return
		}
	}
}

func main() {
	part1(input)
	part2(input)
}

const input = ``
